import React, { Component, useState } from 'react';
import axios from 'axios';
import {Progress} from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import base64 from 'react-native-base64'

class App extends Component {
  constructor(props) {
    super(props);
      this.state = {
        selectedFile: null,
        loaded:0
      }

  }

  checkMimeType=(event)=>{
    //getting file object
    let files = event.target.files 
    //define message container
    let err = []
    // list allow mime type
   const types = ['image/png', 'image/jpeg', 'image/gif']
    // loop access array
    for(var x = 0; x<files.length; x++) {
     // compare file type find doesn't matach
         if (types.every(type => files[x].type !== type)) {
         // create error message and assign to container   
         err[x] = files[x].type+' is not a supported format.\n';
       }
     };
     for(var z = 0; z<err.length; z++) {// if message not same old that mean has error 
         // discard selected file
        toast.error(err[z])
        event.target.value = null
    }
   return true;
  }
  maxSelectFile=(event)=>{
    let files = event.target.files
        if (files.length > 3) { 
           const msg = 'Only 3 images can be uploaded at a time'
           event.target.value = null
           toast.warn(msg)
           return false;
      }
    return true;
 }
 checkFileSize=(event)=>{
  let files = event.target.files
  let size = 2000000 
  let err = []; 
  for(var x = 0; x<files.length; x++) {
  if (files[x].size > size) {
   err[x] = files[x].type+'is too large, please pick a smaller file.\n';
 }
};
for(var z = 0; z<err.length; z++) {// if message not same old that mean has error 
  // discard selected file
 toast.error(err[z])
 event.target.value = null
}
return true;
}
onChangeHandler=event=>{
  var files = event.target.files
  if(this.maxSelectFile(event) && this.checkMimeType(event) &&    this.checkFileSize(event)){ 
  // if return true allow to setState
     this.setState({
     selectedFile: files,
     loaded:0
  })
}
}
  onClickHandler = () => {
    const data = new FormData()
    if(this.state.selectedFile != null){ 
    for(var x = 0; x<this.state.selectedFile.length; x++) {
      data.append('file', this.state.selectedFile[x])
    }
    axios.post("http://localhost:8000/upload", data, {
      onUploadProgress: ProgressEvent => {
        this.setState({
          loaded: (ProgressEvent.loaded / ProgressEvent.total*100),
        })
      },
    })
      .then(res => { // then print response status
        var base = ''
        let result, confidence;
        let outputJSON;
        toast.success('Upload Successful')

        axios.defaults.headers.common['Content-Type'] = "application/json";
        axios.defaults.headers.common['Authorization'] = "Bearer ya29.c.Ko8BvweiEswHjnW0B4ym9z1sfuQ6qpa3R8GYdpxYa67pHOhFULl6VmUVVS4N15j0QcA4wKULUpGbb2rng50AA53VEJVl33P2tBzu4HYFce9IwTSMfljBzLNsi8Qem87KjoPVaknOCt8sR_w7VwnEdkAggHnEb32zILlZ7qrueDa1M-L6bngebz_PPg83UWMgyvg"/* Copy token key here */;

        axios.post("https://automl.googleapis.com/v1beta1/projects/766644774605/locations/us-central1/models/ICN5802549470285529088:predict",
            {
                "payload": {
                    "image": {
                        "imageBytes": base
                    }
                }
            }).then(function(res){
                console.log(res);
                if (Object.keys(res.data).length == 0){
                    result = 'trash'
                    confidence = 99;
                } else{
                    result = res.data.payload[0].displayName;
                    confidence = res.data.payload[0].classification.score;
                    confidence = confidence * 100;
                    confidence = Math.round(confidence);
                }

                let temp = {
                    "result" : result,
                    "confidence" : confidence
                };

                temp = JSON.stringify(temp);

                outputJSON = JSON.parse(temp);
                console.log(outputJSON);
            //     fs.writeFile('output.json', JSON.stringify(outputJSON), 'utf8', (err, data) => {
            //         if (err) console.log(err);
            //         else console.log('written');
            // });
        }).catch(function(err) {
            console.log(err);
        });
      })
      .catch(err => { // then print response status
        toast.error('Upload Failed')
      })
    }
    else{
      toast.error('No file selected.')
    }
  }

  render() {
    return (
      <div className="container">
	      <div className="row">
      	  <div className="offset-md-3 col-md-6">
               <div className="form-group files">
                <label>Upload Your File </label>
                <input type="file" class="form-control" multiple onChange={this.onChangeHandler}/>
              </div>  
              <div className="form-group">
              <ToastContainer />
              <Progress max="100" color="success" value={this.state.loaded} >{Math.round(this.state.loaded,2) }%</Progress>

              </div> 

              <button type="button" className="btn btn-success btn-block" onClick={this.onClickHandler}>Upload</button>

	      </div>
      </div>
      </div>
    );
  }
}

export default App;
