import React, { Component, useState } from 'react';
import axios from 'axios';
import { Progress } from 'reactstrap';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import base64 from 'react-native-base64'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedFile: null,
      loaded: 0
    }

  }

  checkMimeType = (event) => {
    //getting file object
    let files = event.target.files
    //define message container
    let err = []
    // list allow mime type
    const types = ['image/png', 'image/jpeg', 'image/gif']
    // loop access array
    for (var x = 0; x < files.length; x++) {
      // compare file type find doesn't matach
      if (types.every(type => files[x].type !== type)) {
        // create error message and assign to container
        err[x] = files[x].type + ' is not a supported format.\n';
      }
    };
    for (var z = 0; z < err.length; z++) {// if message not same old that mean has error
      // discard selected file
      toast.error(err[z])
      event.target.value = null
    }
    return true;
  }
  maxSelectFile = (event) => {
    let files = event.target.files
    if (files.length > 3) {
      const msg = 'Only 3 images can be uploaded at a time'
      event.target.value = null
      toast.warn(msg)
      return false;
    }
    return true;
  }
  checkFileSize = (event) => {
    let files = event.target.files
    let size = 2000000
    let err = [];
    for (var x = 0; x < files.length; x++) {
      if (files[x].size > size) {
        err[x] = files[x].type + 'is too large, please pick a smaller file.\n';
      }
    };
    for (var z = 0; z < err.length; z++) {// if message not same old that mean has error
      // discard selected file
      toast.error(err[z])
      event.target.value = null
    }
    return true;
  }
  onChangeHandler = event => {
    var files = event.target.files
    if (this.maxSelectFile(event) && this.checkMimeType(event) && this.checkFileSize(event)) {
      // if return true allow to setState
      this.setState({
        selectedFile: files,
        loaded: 0
      })
    }
  }
  onClickHandler = () => {
    const data = new FormData()
    if (this.state.selectedFile != null) {
      for (var x = 0; x < this.state.selectedFile.length; x++) {
        data.append('file', this.state.selectedFile[x])
      }
      axios.post("http://localhost:8000/upload", data, {
        onUploadProgress: ProgressEvent => {
          this.setState({
            loaded: (ProgressEvent.loaded / ProgressEvent.total * 100),
          })
        },
      })
          .then(res => { // then print response status

            toast.success('Upload Successful')


          })
          .catch(err => { // then print response status
            toast.error('Upload Failed')
          })
    }
    else {
      toast.error('No file selected.')
    }
  }
  onAnalyzeHandler = () => {
    const image2base64 = require('image-to-base64');
    let base;
    image2base64("/picture.jpeg") // you can also to use url
        .then(
            (response) => {
              base = response;
              let result, confidence;
              let outputJSON;
              axios.defaults.headers.common['Content-Type'] = "application/json";
              axios.defaults.headers.common['Authorization'] = "Bearer ya29.c.Ko8BvwdU0zqZ6KssmINTbAGfuEGIaX7b075aWwVeR2TRDdAEK2McshohZoyeonJQFCN-AYCoIgSaCKoJKFKYxWnuZgfOJ0FXx1fWTcdMVXmXJMMmMSIOuIofu4iQsf0PihwqlUY30RUcKc3njVTyu5wQOkW2VMqNFWDprVZ7XTJU5W6jOj2DuRJcKYNBKLQrSS4"
              /* Copy token key here */;

              axios.post("https://automl.googleapis.com/v1beta1/projects/766644774605/locations/us-central1/models/ICN5802549470285529088:predict",
                  {
                    "payload": {
                      "image": {
                        "imageBytes": base
                      }
                    }
                  }).then(function (res) {
                console.log(res);
                if (Object.keys(res.data).length === 0) {
                  result = 'trash'
                  confidence = 99;
                } else {
                  result = res.data.payload[0].displayName;
                  confidence = res.data.payload[0].classification.score;
                  confidence = confidence * 100;
                  confidence = Math.round(confidence);
                }

                let temp = {
                  "result": result,
                  "confidence": confidence
                };
                toast.info('I am ' + confidence + '% confident that this is '+result+'.', {
                  position
                });
                temp = JSON.stringify(temp);

                outputJSON = JSON.parse(temp);
                console.log(outputJSON);
              }).catch(function (err) {
                console.log(err);
              });

            }
        )
        .catch(
            (error) => {
              console.log(error); //Exepection error....
            }
        )

    //var base = ''


  }
  render() {
    return (
        <div className="container">
          <div className="row">
            <div>
              <meta httpEquiv="Content-Type" content="text/html; charset=utf-8" />
              <title>2 Rows, 2 Columns, A</title>
              <style type="text/css" dangerouslySetInnerHTML={{__html: "\n\t\t\n\t\tbody {\n\t\t\tmargin: 0;\n\t\t\tpadding: 0;\n\t\t\toverflow: hidden;\n\t\t\theight: 100%; \n\t\t\tmax-height: 100%; \n\t\t\tfont-family:Sans-serif;\n\t\t\tline-height: 1.5em;\n\t\t}\n\t\t\n\t\t#header {\n\t\t\tposition: absolute;\n\t\t\ttop: 0;\n\t\t\tleft: 0;\n\t\t\twidth: 100%;\n\t\t\theight: 100px; \n\t\t\toverflow: hidden; /* Disables scrollbars on the header frame. To enable scrollbars, change \"hidden\" to \"scroll\" */\n\t\t\tbackground: #BCCE98;\n\t\t}\n\t\t\n\t\t#nav {\n\t\t\tposition: absolute; \n\t\t\ttop: 100px; \n\t\t\tleft: 0; \n\t\t\tbottom: 0;\n\t\t\twidth: 35%;\n\t\t\toverflow: auto; /* Scrollbars will appear on this frame only when there's enough content to require scrolling. To disable scrollbars, change to \"hidden\", or use \"scroll\" to enable permanent scrollbars */\n\t\t\tbackground: #DAE9BC; \t\t\n\t\t}\n\t\t\n\t\t#logo {\n\t\t\tpadding:10px;\n\t\t}\n\t\t\n\t\tmain {\n\t\t\tposition: fixed;\n\t\t\ttop: 0; /* Set this to the height of the header */\n\t\t\tleft: 15%; \n\t\t\tright: 0;\n\t\t\tbottom: 0;\n\t\t\toverflow: auto; \n\t\t\tbackground: #fff;\n\t\t}\n\t\t\n\t\t.innertube {\n\t\t\tmargin: 15px; /* Provides padding for the content */\n\t\t}\n\t\t\n\t\tp {\n\t\t\tcolor: #555;\n\t\t}\n\n\t\tnav ul {\n\t\t\tlist-style-type: none;\n\t\t\tmargin: 0;\n\t\t\tpadding: 0;\n\t\t}\n\t\t\n\t\tnav ul a {\n\t\t\tcolor: darkgreen;\n\t\t\ttext-decoration: none;\n\t\t}\n\t\t\t\t\n\t\t/*IE6 fix*/\n\t\t* html body{\n\t\t\tpadding: 100px 0 0 230px; /* Set the first value to the height of the header and last value to the width of the nav */\n\t\t}\n\t\t\n\t\t* html main{ \n\t\t\theight: 100%; \n\t\t\twidth: 65%; \n\t\t}\n\t\t\n\t\t" }} />
              <header id="header">
                <div id="logo">
                  <h1><b>Swamp Trash</b></h1>
                  <p><b>Using machine learning to identify trash for proper waste management.</b></p>
                  <p>&nbsp;</p>
                </div>
              </header>
              <main>
                <div className="innertube">
                  <h3 align="center"><i>Please select your file, upload, then analyze...</i></h3>
                  <p>&nbsp;</p>
                  <div className="offset-md-3 col-md-6">
                    <div className="form-group files">

                      <label>Upload Your File </label>
                      <input type="file" className="form-control" multiple onChange={this.onChangeHandler} />
                    </div>
                    <div className="form-group">

                      <Progress max="100" color="success" value={this.state.loaded} >{Math.round(this.state.loaded, 2)}%</Progress>

                    </div>
                    <ToastContainer />

                    <button type="button" className="btn btn-success btn-block" onClick={this.onClickHandler}>Upload</button>
                    <button type="button" className="btn btn-success btn-block" onClick={this.onAnalyzeHandler}>Analyze</button>

                  </div>
                  <p />
                </div>
              </main>
              <nav id="nav">
                <div className="innertube">
                  <img height="100%" width="100%" src="https://i.ibb.co/qsBY3Z0/Swamp-Trash-Final-Logo.png"></img>
                  <ul>

                  </ul>
                  <p></p>
                  <h2>Created by:&nbsp;
                  </h2>
                  <ul>
                    <li><a href="https://www.linkedin.com/in/duncanrosss/">Duncan Ross</a></li>
                    <li><a href="https://www.linkedin.com/in/ericderezende/">Eric De Rezende</a></li>
                    <li><a href="https://www.linkedin.com/in/hermalez/">Herman Gonzalez</a></li>
                    <li><a href="https://www.linkedin.com/in/logan-welsh-68b331143/">Logan Welsh</a></li>
                    <li><a href="https://www.linkedin.com/in/ziyad-morsi/">Ziyad Morsi</a></li>
                  </ul>
                  <p></p>
                  <h2>Links:</h2>
                  <ul>
                    <li><a href="https://devpost.com/software/swamp-trash">DEVPOST</a></li>
                    <li><a href="https://github.com/duncan-ross/SwampTrash">GitHub</a></li>
                    <li><a href="https://cloud.google.com/sdk/docs?authuser=4">GCloud Documentation</a></li>
                  </ul>
                </div>
              </nav>
            </div>
          </div>
        </div>
    );
  }
}

export default App;

